import logo from './logo.svg';
import './App.css';
import Category from './Category';
import axios from 'axios'
import { useEffect,useState} from 'react'



function App() {
let[finalCategory,setFinalCategory]=useState([])
let[finalpro,setfinalproduct]=useState([])

  let getCategory=()=>{
    axios.get('https://dummyjson.com/products/categories')
    .then((res)=>res.data)
    .then((finalRes)=>{

      setFinalCategory(finalRes)
    })
  }

  let getProducts=()=>{
    axios.get('https://dummyjson.com/products')
    .then((proRes)=>proRes.data)
    .then((finalRes)=>{
      setFinalProduct(finalRes.products)

    })
  }
  useEffect(()=>{
    getCategory();
    getProducts();
  },[])
  let pitems=finalpro.map((products,index)=>{
    return(
      <productItems key={index} pdat={product}/>
    )
  })


  return (
    <>
      <div className='py-[40px]'>
        <div className='max-w-[1320px] mx-auto'>
          <h1 className='text-center text-[40px] font-bold mb-[30px]'>Our Products</h1>

          <div className='grid-cols-[30%-auto] gap-[20px]'>
            <div className='bg-[#aea9a9]'>
              <Category finalCategory={finalCategory}/>
            </div>
          </div>

          <div className='grid grid-cols-3 gap-4 mt-6'>
            <div className='shadow-lg text-center pb-4'>
              <img src='./images/girl.jpeg' alt='Product' />
              <h4>IPHONE</h4>
              <b>Rs 1000000</b>
            </div>
            <div className='shadow-lg text-center pb-4'>
              <img src='./images/girl.jpeg' alt='Product' />
              <h4>IPHONE</h4>
              <b>Rs 1000000</b>
            </div>
            <div className='shadow-lg text-center pb-4'>
              <img src='./images/girl.jpeg' alt='Product' />
              <h4>IPHONE</h4>
              <b>Rs 1000000</b>
            </div>
          </div>

        </div>
      </div>
    </>
  );
}

export default App;
