import logo from './logo.svg';
import './App.css';
import {useState} from 'react';

function App(){
let [todolist,setTodolist]=useState([])

  let saveToDoList=(event)=>{
    let toname=event.target.toname.value;
    if(!todolist.includes(todolist,toname)){
      let finalDolist=[...todolist,toname,toname]
      setTodolist(finalDolist)
    }
    else{
      alert("ToDo Name Allready Exists...")
    }

    event.preventDefault();
  }
  let list=todolist.map((value,index)=>{
    let todolist={todolist}
    return(
      <ToDoListItems value={value} key={index}indexNumber={index}
      todolist={todolist}
      setTodolist={setTodolist}
      />
    )

  })

  return(
    <div className="App">
      <h1>ToDo List</h1>
      <form onSubmit={saveToDoList}>
        <input type="text" name='toname'/>
        <button>Save</button>
      </form>
      <div className="outerDiv">
        <ul>
          <ToDoListItems/>
        </ul>

      </div>

    </div>
  )
}
export default App;
function ToDoListItems({value,indexNumber,todolist,setTodolist}){
  let[status,setStatus]=useState(false)
  let deleteRow=()=>{
    let finalData=todolist.filter((v,i)=>i!=indexNumber)
    setTodolist(finalData)
  }
  let checkStatus=()=>{
    setStatus(!status)

  }
  return(
    <li className={(status)? 'completode':''} onClick={checkStatus}>{indexNumber+1}{value}<span onClick={deleteRow}>&times;</span></li>
  )
}
